import React, {useState} from 'react';
import { Button, FlatList, StyleSheet, Text, TextInput, View } from 'react-native';

export default function App() {
  const [nome, setNome] = useState("");
  const [numero, setNumero] = useState("");
  const [contatos, setContatos] = useState({});
  const capturarNome = (nome) => {
    setNome(nome);
    console.log("Nome capturado " + nome);
  }
  const capturarNumero = (numero) => {
    setNumero(numero);
    console.log("Numero capturado " + numero);
  }
  const addContato = () => {
    setContatos( contatos => {
      console.log("Contato " + nome + " e " + numero + "foi adicionado!")
      return
      contatos.set(nome, numero)
    }
    )

  }
  return (
    <View style={styles.container}>
      <View>
        <TextInput
        placeholder="Nome do contato..."
        style=""
        onChageText={capturarNome}
      />
      <TextInput
        placeholder="NÃºmero contato..."
        style=""
        onChageText={capturarNumero}
      />
      <View>
        <Button
          title="Adicionar contato"
          onPress={addContato}
        />
      </View>
      </View>
      <View>
        <FlatList
          data={contatos}
          renderItem = { nome, numero => {
            <View>
              <Text>{nome} - {numero}</Text>
            </View>
          }}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
